import { useState } from 'react';

function Certificates() {
    const [isAnimated, setIsAnimated] = useState(false);

    const handleAnimation = () => {
        setIsAnimated(true);
        setTimeout(() => {
            setIsAnimated(false);
        }, 1000); // set animation duration
    };

    return (
        <div
            className={`course-list-container ${isAnimated ? 'animated' : ''}`}
            onClick={handleAnimation}
        >
            <div className="course">Learning PHP</div>
            <div className="course">PHP Essential Training</div>
            <div className="course">PHP with MySQL Essential Training: 1 The Basics</div>
            <div className="course">PHP: Object-Oriented Programming</div>
            <div className="course">Node.js Essential Training</div>
            <div className="course">Learning Node.js</div>
            <div className="course">Advance your Node.js Skills</div>
            <div className="course">React.js Essential Training</div>
            <div className="course">Build Production-Ready Apps in React</div>
            <div className="course">Spring Boot 2.0 Essential Training</div>
            <div className="course">Learning Spring with Spring Boot</div>
        </div>
    );
}
