import { Container,Row,Col } from "react-bootstrap";
import { ArrowRightCircle } from "react-bootstrap-icons";
import headerImg from "../src/assets/img/header-img.svg";
import { useState, useEffect } from "react";
export const Banner = ()=>{
    const [loopNum,setLoopNum]=useState('');
    const [isDeleting,setIsDeleting]=useState(false);
    const toRotate=["Web Develepor","Web Designer","UI/ UX Designer"]
    const [text,setText]=useState('');
    const period=2000;
    const[delta,setDelta]=useState(300-Math.random()*100);
    useEffect(()=>{
        let ticker=setInterval(()=>{
            ticker();

        },delta)

        return()=>{clearInterval(ticker)};

    },[text])

    const tick = ()=>{
        let i = loopNum % toRotate.length
        let fullText= toRotate[i];
        let updatedText = isDeleting ? fullText.substring(0,text.length-1) : fullText.substring(0,text.length+1)
        setText(updatedText);
        if(isDeleting){
            setDelta(prevDelta=>prevDelta /2)
        }
        if(!isDeleting && updatedText ===fullText){
            setIsDeleting(true);
            setDelta(period);
        }else if(isDeleting && updatedText ===''){
            setIsDeleting(false);
            setLoopNum(loopNum + 1);
            setDelta(500);
        }
    }


    return(
        <section className="banner" id="home">
            <Container>
                <Row className="align-items-center">
                 <Col xs={12} md={10} xl={5}>
                    <span className="tagline">Welcome to my Portfolio</span>
                    <h1>{`Hello, My name is Onur Korkmaz, I have a three years of experiences which are back-end and front-end Developer`}<span className="wrap">{text}</span></h1>
                    <p>
                    Lorem ipsum dolor sit amet, consectetuer adipiscing elit, sed diam nonummy nibh euismod tincidunt ut laoreet dolore magna aliquam erat volutpat
                    </p>
                </Col>
                <Col xs={12} md={6} xl={5}>
                    <img src={headerImg} alt="Headder Img" />

                /</Col>
                </Row>
            </Container>
        </section>
    )
}
